import './assets/css/app.css';
import Video from './components/Video.js';
import React, {useState, useEffect} from "react"
import axios from "./components/Axios.js"
import ProgressBar from 'react-bootstrap/ProgressBar'
import Navbar from './components/Navbar.js';
import Header from "./components/Header.js";
import Timer from "./components/Timer.js";

function App() {
  const [videos, setVideos] = useState([]); 

  const [progress, setProgress] = useState([
    {
      id: 1,
      progress: 70,
    }
  ], [])

  var startTime = 0;

  useEffect(() => {
    async function fetchPosts() {
      const response = await axios.get('/v2/posts')
      setVideos(response.data);

      return response;
    }

    fetchPosts();

    async function updateProgress() {
      setProgress(startTime / 1000);
      return 0;
    }

    updateProgress();
  }, [])

  const updateProgress = () => {
    setProgress([{id: 1, progress: 45}])
    console.log(progress)
  }

  const componentDidMount = () => {
    /* On init set the start time
       Also: multiplying new Date() by 1 will return a timestamp
     */
    startTime = new Date() * 1;
 }

 componentDidMount();
 
 const componentWillUnmount = () => {
     /* Then on view destroy set the endTime */
     const endTime = new Date() * 1;
     /* Subtract the end time with start time, since endTime has greater value. The result
      is the difference between start and end time in milliseconds.
      */
     const elapsed = endTime - startTime;
     /* The result is in milliseconds so:
        elapsed / 1000 -> is seconds
        elapsed / 1000 / 60 -> is minutes
        etc.
      */

        console.log(elapsed)
        startTime = 0;
 };

  return (
    <div className="app">
      <div className="app__videos">
          <Header />
          {videos.map((video) => (
          <Video 
          key = {video.name}
          url={video.url}
          channel = {video.channel}
          description = {video.description}
          song = {video.song}
          likes = {video.likes}/>
        ))}

      <div className="progressBar">
        <ProgressBar animated now={progress[0].progress} />
      </div>
      </div>
      <Navbar />
      <button onClick={componentWillUnmount}>Bye</button>
      <Timer />
    </div>
  );
}

export default App;
